const Convert = require("./lib/Encryption/convert-binary.js")
const Xor = require("./lib/Encryption/convert-xor.js")
const Decryption = require("./lib/Decryption/decryption.js")

exports.Convert = Convert;
exports.Xor = Xor;
exports.Decryption = Decryption;

function Encryption(input, key) {
    var binary = Convert(input, key)
    var xorbinary = Xor(binary, key)
    return xorbinary;
}

exports.Encryption = Encryption;