module.exports = function Decryption(input, key) {
    input = input.split(",")
    var output = []
    var outputString = ""
    for(var i = 0; i < input.length; i++) {
        output[i] = (("0b" + Number(input[i]).toString(2)) ^ key).toString(2);
        output[i] = String.fromCharCode("0b" + output[i])
        outputString += output[i]
    }
    return outputString;
}